CommonData.BannerCustomCSS:
/***
      Cookie banner - custom style
      Screen 1 
  ***/

#onetrust-banner-sdk {
  border-radius: 8px;
  margin: 0 15px 15px;
}

#onetrust-banner-sdk.otFlat {
  z-index: 49;
}

#onetrust-policy-title {
  font-size: 18px;
}

@media only screen and (max-width: 896px) {
  #onetrust-button-group-parent {
    position: sticky !important;
    bottom: 0 !important;
    background: #fff !important;
  }
}

@media only screen and (min-width: 897px) {
  #onetrust-close-btn-container {
    top: 10% !important;
  }
}

@media only screen and (min-width: 1024px) {
  #onetrust-banner-sdk:not(.ot-iab-2) #onetrust-group-container {
    width: 70%
  }
  #onetrust-banner-sdk:not(.ot-iab-2).vertical-align-content #onetrust-button-group-parent {
    left: 70%;
    width: 30%;
  }
}

#onetrust-consent-sdk #onetrust-banner-sdk {
  max-height: 80%;
}

#onetrust-consent-sdk #onetrust-banner-sdk *:focus,
#onetrust-consent-sdk #onetrust-banner-sdk:focus {
  outline: none;
}

#onetrust-consent-sdk #onetrust-policy-text .onetrust-description {
  display: block;
  margin-bottom: 8px;
}

#onetrust-banner-sdk #onetrust-policy-text a {
  font-size: 14px;
  font-weight: initial;
}

#onetrust-banner-sdk #onetrust-policy-text a:first-of-type {
  margin-left: 0;
}

#onetrust-banner-sdk #onetrust-reject-all-handler {
  background-color: #7ebbaf;
  border-color: #7ebbaf;
}

#onetrust-consent-sdk #onetrust-accept-btn-handler,
#onetrust-banner-sdk #onetrust-reject-all-handler {
  width: 100%;
}

#onetrust-consent-sdk #onetrust-accept-btn-handler,
#onetrust-banner-sdk #onetrust-reject-all-handler {
  font-weight: 400;
  font-size: 14px;
  padding: 10px 16px;
  border-radius: 8px;
}

#onetrust-banner-sdk #onetrust-pc-btn-handler.cookie-setting-link {
  text-decoration: none;
}

#onetrust-banner-sdk #onetrust-button-group-parent .cookie-setting-link {
  font-size: 14px;
  font-weight: initial;
}

/* Hidden ot logo and floating logo */
#onetrust-pc-sdk .ot-pc-footer-logo a,
#ot-sdk-btn-floating.ot-floating-button,
#onetrust-pc-sdk .ot-pc-footer-logo,
#ot-sdk-btn-floating.ot-floating-button button,
#ot-sdk-btn-floating .ot-floating-button__back {
  visibility: hidden;
}

#onetrust-pc-sdk .ot-pc-footer-logo a {
  display: none;
}

#onetrust-pc-sdk[_ngcontent-wun-c56]
  .save-preference-btn-handler[_ngcontent-wun-c56] {
  text-decoration: none;
}

/* update cookie settings button */
#ot-sdk-btn.ot-sdk-show-settings,
#ot-sdk-btn.optanon-show-settings {
  color: inherit;
  font-size: inherit;
  padding: 0;
  line-height: inherit;
  border: none;
  background: none;
  transition: none;
}

#ot-sdk-btn.ot-sdk-show-settings:hover {
  background: none;
}

CommonData.PCCustomCSS:
/***
      Cookie banner - custom style
      Screen 2 
  ***/

/* logo */
#onetrust-pc-sdk {
  border-radius: 8px !important;
}

#onetrust-pc-sdk .ot-pc-logo {
  display: none;
}

#ot-pc-content #ot-pc-title,
#ot-pc-content #ot-category-title {
  font-weight: 600;
  font-size: 18px;
}

#onetrust-pc-sdk .ot-pc-header {
  border-bottom: none;
}

#onetrust-pc-sdk #filter-btn-handler,
#onetrust-pc-sdk .ot-pc-footer-logo,
#onetrust-pc-sdk .ot-fltr-cntr {
  display: none;
}
#onetrust-pc-sdk #accept-recommended-btn-handler {
    margin-right: 20px;
}

#onetrust-pc-sdk .ot-pc-refuse-all-handler,
#onetrust-pc-sdk .save-preference-btn-handler {
    margin-right: 10px;
}

#onetrust-pc-sdk #accept-recommended-btn-handler,
#onetrust-pc-sdk .ot-pc-refuse-all-handler,
#onetrust-pc-sdk .save-preference-btn-handler {
  padding: 10px 16px;
}

#onetrust-pc-sdk .ot-pc-refuse-all-handler {
  background-color: #7ebbaf !important;
  border-color: #7ebbaf !important;
}

#onetrust-pc-sdk #vendor-search-handler {
  border-color: var(--border-primary-gray);
  border-radius: var(--border-radius-l);
}

#onetrust-pc-sdk #vendor-search-handler:focus {
  border: 1px solid var(--background-button-primary-pressed) !important;
}

/* switch */
#ot-pc-content .ot-tgl input:checked + .ot-switch .ot-switch-nob {
  background-color: var(--background-button-primary-pressed);
  border-color: var(--background-button-primary-pressed);
}

#ot-pc-content .ot-switch-nob {
  background-color: #dadada;
  border-color: #dadada;
}

#ot-pc-content .ot-tgl input:checked + .ot-switch .ot-switch-nob:before {
  background-color: #fff;
  left: 1px;
}

/* accordion */
#ot-pc-content .ot-accordion-layout .ot-cat-header {
  font-weight: 600;
  font-size: 12px;
}

#onetrust-pc-sdk .ot-accordion-layout.ot-cat-item {
  border-right: none;
  border-left: none;
  border-radius: 0;
}

#ot-pc-content #ot-pc-desc .privacy-notice-link,
#ot-pc-content .ot-accordion-layout .ot-link-btn {
  font-size: 14px;
}

#onetrust-pc-sdk .ot-cat-grp .ot-always-active {
  font-weight: 600;
  color: var(--background-button-primary-pressed);
}

#onetrust-consent-sdk
  #onetrust-pc-sdk
  button:not(#clear-filters-handler):not(.ot-close-icon):not(#filter-btn-handler):not(.ot-remove-objection-handler):not(.ot-obj-leg-btn-handler):not([aria-expanded]):not(.ot-link-btn) {
  font-size: 14px;
  font-weight: 400;
  height: 36px;
  border-radius: 8px;
}

#onetrust-consent-sdk #onetrust-pc-sdk *:focus,
#onetrust-consent-sdk #onetrust-pc-sdk .ot-vlst-cntr > a:focus {
  outline: none;
  border: none;
  text-decoration: underline;
}

#onetrust-pc-sdk .save-preference-btn-handler {
  background-color: #fff !important;
  color: var(--background-button-primary-pressed) !important;
}

@media screen and (max-width: 640px) {
  #onetrust-pc-sdk.otPcCenter {
    height: 96% !important;
    min-width: 94%;
    margin: 3%;
  }
  #onetrust-pc-sdk .ot-btn-subcntr {
    display: block;
  }
  #onetrust-pc-sdk .ot-pc-footer {
    display: flex;
    flex-direction: column;
  }
}

#onetrust-pc-sdk .ot-host-item > button:focus,
#onetrust-pc-sdk .ot-pc-footer {
  border-top: none;
}

/***
      Cookie banner - custom style
      Screen 3
  ***/
#onetrust-pc-sdk #ot-lst-title h3 {
  font-weight: 600;
  font-size: 18px;
}

#onetrust-pc-sdk .ot-chkbox input:checked ~ label::before {
  background-color: var(--background-button-primary-pressed);
}

#onetrust-pc-sdk .ot-chkbox label::before {
  border-color: var(--background-button-primary-pressed);
}

#onetrust-pc-sdk .ot-acc-hdr .ot-host-name {
  font-weight: 600;
}

#onetrust-pc-sdk .ot-acc-txt .ot-host-info {
  font-size: 16px;
}

#onetrust-pc-sdk .ot-sel-all {
  padding-right: 10px;
}

#onetrust-pc-sdk #ot-pc-lst .ot-tgl-cntr {
  right: 0
}

#onetrust-pc-sdk.ot-ftr-stacked .ot-pc-footer button {
  width: auto;
}

#ot-pc-desc #ot-privacy-policy-link, #ot-pc-desc #ot-imprint-link {
  color: #32a48d;
  font-size: 14px;
}
#ot-pc-desc #ot-privacy-policy-link {
 margin-left: 0 !important;
}
